    function toggleMobileNavbar() {
        const mobileNavContainer = document.querySelector("#mobile-navbar");

        if (mobileNavContainer.classList.contains("scale-y-0")) {
            mobileNavContainer.classList.replace("scale-y-0", "scale-y-100");
            mobileNavContainer.classList.replace("h-0", "h-fit");
            mobileNavContainer.classList.replace("opacity-0", "opacity-100");
        } else {
            mobileNavContainer.classList.replace("scale-y-100", "scale-y-0");
